import Form from "./form";
import Todo from "./todo";
import React, { useState } from "react";
import axios from "axios";





function App(props, posts) {
    var posts
    axios.get("http://localhost:8000/api/todos/")
        .then((response) => {
            // console.log(response.data, this)
            posts = response.data
        })

    const [tasks, setTasks] = useState(posts);
    console.log(posts)


    function toggleTaskCompleted(id) {
        const updatedTasks = tasks.map(task => {
            // if this task has the same ID as the edited task
            if (id === task.id) {
                // use object spread to make a new object
                // whose `completed` prop has been inverted
                axios
                    .patch(`/api/todos/${id}/`, {"completed": !task.completed})
                return {...task, completed: !task.completed}
            }
            return task;
        });

        setTasks(updatedTasks);
    }
    function editTask(id, newName) {
        const editedTaskList = tasks.map(task => {
            // if this task has the same ID as the edited task
            if (id === task.id) {
                //
                return {...task, name: newName}
            }
            return task;
        });
        axios
        .patch(`/api/todos/${id}/`, {"title": newName})
        setTasks(editedTaskList);
    }


    function deleteTask(id) {
        const remainingTasks = tasks.filter(task => id !== task.id);
        setTasks(remainingTasks);
        axios
            .delete(`/api/todos/${id}/`, {"unique": id})
    }





    const taskList = tasks.map(task => (
        <Todo
            id={task.id}
            name={task.name}
            completed={task.completed}
            key={task.id}
            toggleTaskCompleted={toggleTaskCompleted}
            deleteTask={deleteTask}
            editTask={editTask}
        />
    ));




    function addTask(name) {
        const newTask = { id: Date.now() , name: name, completed: false };
        setTasks([...tasks, newTask]);
        axios
            .post("/api/todos/", {"unique": newTask.id, "title": newTask.name, "completed": newTask.completed})


    }


    return (
        <div className="todoapp stack-large">
            <h1>Test</h1>
            <Form addTask={addTask} />

            <ul
                role="list"
                className="todo-list stack-large stack-exception"
                aria-labelledby="list-heading"
            >
                {taskList}
            </ul>
        </div>
    );
}

export default App;
