import React, { useState } from "react";
import './index';

function App(props) {


    const [tasks] = useState(props.tasks);
    var numero = 0
    numero = numero + 1
    console.log(tasks)
    return (
        <div className="col"> 
            {
            tasks.map(task => 
                
            <div class="chain">{"Nombre:\n" + task.data + "\n" + "Hash:" + task._id + "\n"+ "Prev:" + task.prev + "\n"+" Tipo: " + numero}  <hr className="dashed"/> </div>)
            }
        </div>
    );

}

function selectElement(selector){
    return document.querySelector(selector);
}
//Limpiar el contenido
function clearResult(){
    selectElement('.search-results').innerHTML="";
}

function getResults(props){
    const search = selectElement('.searchbar').value;
    console.log(props.tasks)
    clearResult();
    if(search.length > 0){
        props.tasks.map(task =>{
        if (
            task.carName.toLocaleLowerCase().includes(search.toLocaleLowerCase()) ||
            task.carCode.toLocaleLowerCase().includes(search.toLocaleLowerCase())
            ){
                selectElement('.search-results').innerHTML+=`
                <div class = "search-results-item">
                    <span class= "search-item">${task.carName}</span>
                    <span class= "search-item">${task.carPrice}</span>
                    <span class= "search-item">${task.carCode}</span>
                </div>
                `;
            }
        });
    }

}

selectElement('.searchbar').addEventListener('keyup', getResults);

    export default App;
