import React, { useState } from "react";


export default function Todo(props) {
    const [isEditing, setEditing] = useState(false);
    const [newName, setNewName] = useState('');



    const viewTemplate = (
        <div className="stack-small">
            <div className="c-cb">
                <input
                    id={props._id}
                    type="checkbox"
                    defaultChecked={props.completed}
                    onChange={() => props.toggleTaskCompleted(props._id)}
                />
                <label className="todo-label" htmlFor={props._id}>
                    {props.name}
                </label>
            </div>
            <div className="btn-group">
                <button type="button" className="btn" onClick={() => setEditing(true)}>
                    Edit <span className="visually-hidden">{props.data}</span>
                </button>
                <button
                    type="button"
                    className="btn btn__danger"
                    onClick={() => props.deleteTask(props._id)}
                >
                    Delete <span className="visually-hidden">{props.data}</span>
                </button>
            </div>
        </div>
    );
  


    return <li className="todo">{isEditing ? editingTemplate : viewTemplate}</li>;
    }
